        public static int GetBugsInfoArrayLength()
        {
            return BugsInfoArray.Length;
        }

        public static Tuple<BugsInfo, int> GetBugCheckByName(string name)
        {
            for (int i = 0; i < GetBugsInfoArrayLength(); i++)
                if (BugsInfoArray[i].Name.Equals(name))
                    return new Tuple<BugsInfo, int>(BugsInfoArray[i], 1);

            return new Tuple<BugsInfo, int>(null, 0);
        }

        public static Tuple<BugsInfo, int> GetBugCheckByIndex(int index)
        {
            for (int i = 0; i < GetBugsInfoArrayLength(); i++)
                if (BugsInfoArray[i].Index.Equals(index))
                    return new Tuple<BugsInfo, int>(BugsInfoArray[i], 1);

            return new Tuple<BugsInfo, int>(null, 0);
        }
    }
